mapper.select:
SELECT COUNT(post_number)
FROM posts p
WHERE p.user_number = #{userNumber}
and post_number = #{postNumber}

mapper.update:
UPDATE posts
SET post_name = #{postName},
content = #{content}
WHERE post_number = #{postNumber}

UPDATE posts
SET view = view + 1
WHERE post_number = #{postNumber}

mapper.select:
SELECT post_number, category_number, user_number, post_name, admin_post, content, create_time, suggestion_count, view
FROM posts
WHERE post_number = #{postNumber}

SELECT comment_number, post_number, content, user_id, create_time
FROM comments
WHERE comment_number = #{commentNumber}

mapper.delete:
DELETE FROM posts
WHERE post_number = #{postNumber}
and user_number = #{userNumber}
